BEFORE INSERT ON `mi_base_datos`.`contratos`
FOR EACH ROW
BEGIN
    DECLARE existe INT;

    SELECT COUNT(*) INTO existe
    FROM contratos
    WHERE InmuebleID = NEW.InmuebleID
      AND Estado = 'Vigente'
      AND (
            (NEW.FechaInicio BETWEEN FechaInicio AND FechaFin) OR
