      "Index.cshtml": "@model List<InmobiliariaApp.Models.Inmueble>
@{
    ViewData["Title"] = "Gestión de Inmuebles";
}

<h1>@ViewData["Title"]</h1>

@if (TempData["Mensaje"] != null)
{
    <div class="alert alert-success">@TempData["Mensaje"]</div>
}

<a asp-action="Edicion" asp-route-id="0" class="btn btn-success mb-3">Crear Nuevo Inmueble</a>

<table class="table table-bordered">
    <thead>
        <tr>
            <th>Dirección</th>
            <th>Metros²</th>
            <th>Tipo</th>
            <th>Precio</th>
            <th>Propietario</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        @foreach (var item in Model)
        {
            <tr>
                <td>@item.Direccion</td>
                <td>@item.MetrosCuadrados</td>
                <td>@item.Tipo</td>
                <td>@item.Precio.ToString("C")</td>
                <td>@item.NombrePropietario</td>

                <td>
                    <a asp-action="Edicion" asp-route-id="@item.Id" class="btn btn-warning btn-sm">Editar</a>
                    <button type="button" class="btn btn-danger btn-sm" data-bs-toggle="modal" data-bs-target="#confirmModal"
                            data-id="@item.Id" data-direccion="@item.Direccion">
                        Eliminar
                    </button>
                </td>
            </tr>
        }
    </tbody>
</table>

<!-- Modal de confirmación -->
<div class="modal fade" id="confirmModal" tabindex="-1" aria-labelledby="modalLabel" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <form asp-action="Eliminar" method="post">
                <div class="modal-header">
                    <h5 class="modal-title" id="modalLabel">Confirmar eliminación</h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Cerrar"></button>
                </div>
                <div class="modal-body">
                    ¿Seguro que deseas eliminar el inmueble <strong id="inmuebleDireccion"></strong>?
                    <input type="hidden" name="id" id="inmuebleId" />
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancelar</button>
                    <button type="submit" class="btn btn-danger">Eliminar</button>
                </div>
            </form>
        </div>
    </div>
</div>

@section Scripts {
    <script>
        var modal = document.getElementById('confirmModal');
        modal.addEventListener('show.bs.modal', function (event) {
            var button = event.relatedTarget;
            var id = button.getAttribute('data-id');
            var direccion = button.getAttribute('data-direccion');

            document.getElementById('inmuebleId').value = id;
            document.getElementById('inmuebleDireccion').textContent = direccion;
        });
    </script>
}
